function circleGrid = circleWalker(stepSize);reader = fopen('Rayleigh_random.csv');if reader == -1   disp('File open not succesful')else  ray = textscan(reader, '%f',1);  closeresult = fclose(reader);  if closeresult == -1      disp('File close not succesful')  else  endend    stepConstant = 0;numSubSteps = randi([50 1000],1);circleGrid = zeros(numSubSteps,2);stepLength = 0;if nargin == 0  stepLength = 1;  stepConstant = 0;elseif stepSize == -1stepConstant = 1;reader = fopen('Rayleigh_random.csv');if reader == -1   disp('File open not succesful')else  ray = textscan(reader, '%f',1);  closeresult = fclose(reader);  if closeresult == -1      disp('File close not succesful')  else  endend    refMat = [ray];elsestepLength = stepSize;endfor stepper = 1:numSubSteps  if stepConstant == 1    randomRandy = randi([1,10000]);    stepLength = refMat(randomRandy);  else  end   stepAngle = (2 * pi) * rand(1);  y = sin(stepAngle) * stepLength;  x = cos(stepAngle) * stepLength;  circleGrid(stepper + 1,1) = circleGrid(stepper,1) + x;  circleGrid(stepper + 1,2) = circleGrid(stepper,2) + y;endsimulation2placer = uigetdir;file2name = [simulation2placer filesep 'Circle Walker Sim' datestr(now, 'yy-mm-dd HH-MM-SS')];wri2er = fopen([file2name '.csv'], 'wt');if wri2er == -1  disp('File not open correctly');elseendfprintf(wri2er,'x,y\n');fprintf(wri2er, '%d,%d,\n', circleGrid');closeresul2 = fclose(wri2er);if closeresul2 == -1  disp('File did not close correctly');elseendend