stepLength = randi([1 10],1);numSubSteps = randi([50 1000],1);circleGrid = zeros(numSubSteps,2);realCircleGrid = zeros(floor(numSubSteps/stepLength),2);for stepper = 1:numSubSteps   stepAngle = (2 * pi) * rand(1);  y = sin(stepAngle);  x = cos(stepAngle); circleGrid(stepper + 1,1) = circleGrid(stepper,1) + x; circleGrid(stepper + 1,2) = circleGrid(stepper,2) + y;endfor stomper = 1:floor(numSubSteps/stepLength)  realCircleGrid(stomper + 1,:) = circleGrid((stomper*stepLength),:);end           simulation2placer = uigetdir;file2name = [simulation2placer filesep 'Circle Walker Sim' datestr(now, 'yy-mm-dd HH-MM-SS')];wri2er = fopen([file2name '.csv'], 'wt');if wri2er == -1  disp('File not open correctly');elseendfprintf(wri2er,'x,y\n'); fprintf(wri2er, '%d,%d,\n', circleGrid'); closeresul2 = fclose(wri2er);if closeresul2 == -1  disp('File did not close correctly');elseendsimulation3placer = uigetdir;file3name = [simulation3placer filesep 'Real Circle Walker Sim' datestr(now, 'yy-mm-dd HH-MM-SS')];wri3er = fopen([file3name '.csv'], 'wt');if wri2er == -1  disp('File not open correctly');elseendfprintf(wri3er,'x,y\n'); fprintf(wri3er, '%d,%d,\n', realCircleGrid'); closeresul3 = fclose(wri3er);if closeresul3 == -1  disp('File did not close correctly');elseend