stepLength = 5;numSteps = 50;circleGrid = zeros(numSteps,2);for stepper = 1:numSteps +1  stepAngle = (2 * pi) * rand(1);  y = sin(stepAngle);  x = cos(stepAngle); circleGrid(stepper + 1,1) = circleGrid(stepper,1) + x; circleGrid(stepper + 1,2) = circleGrid(stepper,2) + y;endsimulation2placer = uigetdir;file2name = [simulation2placer filesep 'Circle Walker Sim' datestr(now, 'yy-mm-dd HH-MM-SS')];wri2er = fopen([file2name '.csv'], 'wt');if wri2er == -1  disp('File not open correctly');elseendfprintf(wri2er,'x,y\n'); fprintf(wri2er, '%d,%d,\n', circleGrid'); closeresul2 = fclose(wri2er);if closeresul2 == -1  disp('File did not close correctly');elseend