function circleGrid = circleWalker(stepSize)lowerRadius = 5;circleWidth = 2 * pi * lowerRadius;leftBound = circleWidth / 2;rightBound = -circleWidth / 2;stepConstant = 0;numSubSteps = 10000;circleGrid = zeros(numSubSteps,2);stepLength = 0;if nargin == 0    stepLength = 1;    stepConstant = 0;elseif stepSize == -1    stepConstant = 1;else    stepLength = stepSize;endfor stepper = 1:numSubSteps    if stepConstant == 1        stepLength = sdStepLength;    else    end    stepAngle = (2 * pi) * rand(1);    y = sin(stepAngle) * stepLength;    x = cos(stepAngle) * stepLength;    circleGrid(stepper + 1,1) = circleGrid(stepper,1) + x;    circleGrid(stepper + 1,2) = circleGrid(stepper,2) + y;    if circleGrid(stepper+1,1) > leftBound        circleGrid(stepper+1,1) = circleGrid(stepper + 1,1) - circleWidth;        circleGrid(stepper+1,2) = circleGrid(stepper + 1,2);    elseif  circleGrid(stepper+1,1) < rightBound        circleGrid(stepper+1,1) = circleGrid(stepper + 1,1) + circleWidth;        circleGrid(stepper+1,2) = circleGrid(stepper+1,2);    else    end%     currX = circleGrid(stepper+1,1);%     prevX = circleGrid(stepper,1);endsimulation2placer = uigetdir;file2name = [simulation2placer filesep 'Circle Walker Sim' datestr(now, 'yy-mm-dd HH-MM-SS')];wri2er = fopen([file2name '.csv'], 'wt');if wri2er == -1    disp('File not open correctly');elseendfprintf(wri2er,'x,y\n');fprintf(wri2er, '%d,%d,\n', circleGrid');closeresul2 = fclose(wri2er);if closeresul2 == -1    disp('File did not close correctly');elseendend