function stepLength = sdStepLengthgeter = uigetfile;reader = fopen(geter,'r');if reader == -1   disp('File open not succesful')else  ray = textscan(reader, '%f');  closeresult = fclose(reader);  if closeresult == -1      disp('File close not succesful')  else  endend    refMat = ray{1,1}(:,1);stepLength = refMat(randi([1 1000]))