numvariable = 10000;rMat = zeros(numvariable,1);for sam = 1:numvariablex = randn(1);y = randn(1);rMat(sam,1) = sqrt(x^2 + y^2);endsimulationPlacer = uigetdir;fileName = [simulationPlacer filesep 'Rayleigh random1' datestr(now, 'yy-mm-dd HH-MM-SS')]; writer = fopen([fileName '.csv'], 'wt');if writer == -1   disp('File not Open Correctly');elseend fprintf(writer, '%d\n', rMat'); closeresult = fclose(writer);if closeresult == -1  disp('File did not close correctly');elseend 