matGrid = gridWalker;numSteps = 20;numSubStep = size(matGrid,1);newMat = zeros(floor(numSubStep/numSteps),2+1); lowerRadius = 5;circleWidth = 2 * pi * lowerRadius;leftBound = circleWidth / 2;rightBound = -circleWidth / 2;r = circleWidth / 2;angleMat = zeros(floor(numSubStep/numSteps),1);thetaMat = zeros(floor(numSubStep/numSteps),1);xMat = zeros(floor(numSubStep/numSteps),1);zMat = zeros(floor(numSubStep/numSteps),1);totalMat = zeros(floor(numSubStep/numSteps),3);for stepper = 1:floor(numSubStep/numSteps)  newMat(stepper + 1,:) = matGrid((stepper*numSteps),:);  angleMat(stepper,1) = leftBound - newMat(stepper,1);    thetaMat(stepper,1) = (angleMat(stepper,1) / circleWidth) * (2 * pi);    xMat(stepper,1) = r * sin(thetaMat(stepper,1));    zMat(stepper,1) = r * cos(thetaMat(stepper,1));    totalMat(stepper,1) = xMat(stepper,1);    totalMat(stepper,3) = zMat(stepper,1);    totalMat(stepper,2) = newMat(stepper,2);end  simulationPlacer = uigetdir;% fileName = [simulationPlacer filesep 'GridWalker ' num2str(numSteps) ' Step ' datestr(now, 'yy-mm-dd HH-MM-SS')]; fileName = [simulationPlacer filesep 'GridWalker ' num2str(numSteps) ' 2D Step']fileName2 = [simulationPlacer filesep 'GridWalker ' num2str(numSteps) ' 3D Step']writer = fopen([fileName '.csv'], 'wt');writer2 = fopen([fileName '.csv'], 'wt');if writer == -1   disp('File not Open Correctly');elseendif writer2 == -1    disp('File not open correctly')elseend fprintf(writer,'x,y\n'); fprintf(writer, '%d,%d,\n', newMat'); fprintf(writer2,'x,y\n'); fprintf(writer2, '%d,%d,\n', totalMat'); closeresult = fclose(writer);if closeresult == -1  disp('File did not close correctly');elseendcloseresult2 = fclose(writer2);if closeresult2 == -1    disp('File did not close correctly');elseend