function ouputdata = stepFrequencymatGrid = gridWalker;numSteps = 20;numSubStep = size(matGrid,1);outputdata.newMat = zeros(floor(numSubStep/numSteps),2); lowerRadius = 5;circleWidth = 2 * pi * lowerRadius;leftBound = circleWidth / 2;rightBound = -circleWidth / 2;r = circleWidth / 2;angleMat = zeros(floor(numSubStep/numSteps),1);thetaMat = zeros(floor(numSubStep/numSteps),1);xMat = zeros(floor(numSubStep/numSteps),1);zMat = zeros(floor(numSubStep/numSteps),1);outputdata.totalMat = zeros(floor(numSubStep/numSteps),3);for stepper = 1:floor(numSubStep/numSteps)  outputdata.newMat(stepper + 1,:) = matGrid((stepper*numSteps),:);  angleMat(stepper,1) = leftBound - newMat(stepper,1);    thetaMat(stepper,1) = (angleMat(stepper,1) / circleWidth) * (2 * pi);    xMat(stepper,1) = r * sin(thetaMat(stepper,1));    zMat(stepper,1) = r * cos(thetaMat(stepper,1));    outputdata.totalMat(stepper,1) = xMat(stepper,1);    outputdata.totalMat(stepper,3) = zMat(stepper,1);    outputdata.totalMat(stepper,2) = newMat(stepper,2);end simulationPlacer = uigetdir; fileName = [simulationPlacer filesep 'StepFreq ' num2str(numSteps) ' 2D Step'];fileName2 = [simulationPlacer filesep 'StepFreq ' num2str(numSteps) ' 3D Step'];sfilename = [simulationPlacer filesep 'StepFreq Struct'];writer = fopen([fileName '.csv'], 'wt');writer2 = fopen([fileName2 '.csv'], 'wt');if writer == -1   disp('File not Open Correctly');elseendif writer2 == -1    disp('File not open correctly')else fprintf(writer,'x,y\n'); fprintf(writer, '%d,%d,\n', outputdata.newMat'); fprintf(writer2,'x,y\n'); fprintf(writer2, '%d,%d,%d,\n', totalMat');end closeresult = fclose(writer);if closeresult == -1  disp('File did not close correctly');elseendcloseresult2 = fclose(writer2);if closeresult2 == -1    disp('File did not close correctly');elseendsave(sfilename,'-struct','outputdata');end