matGrid = gridWalker;numSteps = 20;numSubStep = size(matGrid,1);newMat = zeros(floor(numSubStep/numSteps),2); for chooser = 1:floor(numSubStep/numSteps)  newMat(chooser + 1,:) = matGrid(1+(chooser*numSteps),:);endsimulationPlacer = uigetdir;fileName = [simulationPlacer filesep 'GridWalker ' num2str(numSteps) ' Step ' datestr(now, 'yy-mm-dd HH-MM-SS')]; writer = fopen([fileName '.csv'], 'wt');if writer == -1   disp('File not Open Correctly');elseend fprintf(writer,'x,y\n'); fprintf(writer, '%d,%d,\n', newMat'); closeresult = fclose(writer);if closeresult == -1  disp('File did not close correctly');elseend